import { Modal, Button, Paragraph, Form, FormControl, TextInput, Textarea, Stack, Radio, ModalControls } from '@contentful/f36-components';
import React from 'react';
import useMethods from 'use-methods';

const FlagTypes = {
   TEMPORARY: 'temporary',
   PERMANENT: 'permanent'
};

const initialData = ({
   flagName: '',
   featureKey: '',
   description: '',
   type: FlagTypes.TEMPORARY
 });

 const methods = state => {
   return {
      setFlagName(flagName){
         state.flagName = flagName;
      },
      setFeatureKey(featureKey){
         state.featureKey = featureKey;
      },
      setDescription(description){
         state.description = description;
      },
      setType(type){
         state.type = type;
      }
   }
 }

function CreateFeatureFlagModal(props) {

   const globalState = useMethods(methods,initialData);
   const [state, actions] = globalState;

   const createFeatureFlag = () => {
      const featureFlagCreated = {
         name: state.flagName,
         featureKey: state.featureKey,
         description: state.description,
         featureType: state.type,
         id: 123 // Replace this id with contentful id
      };
      props.onModalClose(featureFlagCreated);
   }

  return (
    <React.Fragment>
      <Modal onClose={() => props.onModalClose('')} isShown={props.isShown}>
         {() => (
         <>
            <Modal.Header
               title="Create Feature Flag"
               onClose={() => props.onModalClose()}
            />
            <Modal.Content>
               <Form onSubmit={createFeatureFlag}>
                  <FormControl>
                     <FormControl.Label isRequired>Flag name</FormControl.Label>
                     <TextInput
                        maxLength={30}
                        value={state.flagName}
                        onChange={(e) => actions.setFlagName(e.target.value)}/>
                  </FormControl>
                  <FormControl>
                     <FormControl.Label isRequired>Feature key</FormControl.Label>
                     <TextInput
                        maxLength={30}
                        value={state.featureKey}
                        onChange={(e) => actions.setFeatureKey(e.target.value)}/>
                  </FormControl>
                  <FormControl>
                     <FormControl.Label>Description</FormControl.Label>
                     <Textarea
                        value={state.description}
                        onChange={(e) => actions.setDescription(e.target.value)}/>
                  </FormControl>
                  <FormControl>
                     <FormControl.Label>Feature type</FormControl.Label>
                     <Stack flexDirection='row'>
                        <Radio
                           id='flag-type1'
                           name='flag-type'
                           value={FlagTypes.TEMPORARY}
                           isChecked={state.type === FlagTypes.TEMPORARY}
                           onChange={() => actions.setType(FlagTypes.TEMPORARY)}>Temporary</Radio>
                        <Radio
                           id='flag-type2'
                           name='flag-type'
                           value={FlagTypes.PERMANENT}
                           isChecked={state.type === FlagTypes.PERMANENT}
                           onChange={() => actions.setType(FlagTypes.PERMANENT)}>Permanent</Radio>
                     </Stack>
                  </FormControl>
               </Form>
            </Modal.Content>
            <ModalControls>
            <Button
                size="small"
                variant="transparent"
                onClick={() => props.onModalClose()}
              >
                Close
              </Button>
              <Button
                size="small"
                variant="positive"
                isDisabled={!state.flagName.length || !state.featureKey.length}
                onClick={createFeatureFlag}
              >
                Create
              </Button>
            </ModalControls>
         </>
         )}
      </Modal>
    </React.Fragment>
  )
}

export default CreateFeatureFlagModal;
