import { Flex, Button, Grid, Text, Heading, Paragraph, Stack } from '@contentful/f36-components';
import tokens from '@contentful/f36-tokens';
import { css } from 'emotion';
import { PlusIcon } from '@contentful/f36-icons';
import React, { useCallback } from 'react';
import VariationItem from './VariationItem';

const styles = {
   heading: css({
      marginBottom: '10px'
   }),
   container: css({
    width: '100%'
   })
}

function Variations(props) {

  const addVariation = () => {

  }

  const createVariation = useCallback(async(variation, contentType) => {
    const data = await props.sdk.navigator.openNewEntry(contentType.sys.id,{
      slideIn: true
    });

    const variations = props.sdk.entry.fields.variations.getValue() || [];
    const meta = props.sdk.entry.fields.meta.getValue() || {};

    props.sdk.entry.fields.meta.setValue({
      ...meta,
      [variation.key]: data.entity.sys.id
    });

    props.sdk.entry.fields.variations.setValue([
      ...variations,
      {
        sys: {
          type: 'Link',
          id: data.entity.sys.id,
          linkType: 'Entry'
        }
      }
    ])
    console.log('here: ',props.sdk)
  });

  return (
    <React.Fragment>
      <Flex alignItems='center' justifyContent='space-between'>
        <Heading element='h2' className={styles.heading}>Variations:</Heading>
        <Button variant='primary' size='small' startIcon={<PlusIcon />} onClick={() => addVariation(true)}>Add Variation</Button>
      </Flex>
      <Paragraph>Content created in this feature flag is available only for this feature flag</Paragraph>
      <Flex flexDirection='column' justifyContent='center' marginLeft='spacingM' marginTop='spacingL' gap='spacingXl' className={styles.container}>
        {props.variations.map(variation => {
            return <VariationItem variation={variation} contentTypes={props.contentTypes} onCreateVariation={createVariation} key={variation.id}/>
        })}
      </Flex>
    </React.Fragment>
  )
}

export default Variations;
