import { Button, ButtonGroup, Heading, Modal, Paragraph, IconButton, Select } from '@contentful/f36-components';
import React, {useState} from 'react';
import { css } from 'emotion';
import tokens from '@contentful/f36-tokens';
import { PlusIcon } from '@contentful/f36-icons';
import CreateFeatureFlagModal from './CreateFeatureFlagModal';

const styles = {
   flexContainer: css({
      display: 'flex',
      alignItems: 'center',
      width: '100%',
      justifyContent: 'space-between',
      marginBottom: tokens.spacingL
   }),
   heading: css({
      height: '20px',
      marginRight: '100px'
   }),
   select: css({
      width: '400px'
   }),
   description: css({
     marginTop: tokens.spacingS,
     color: tokens.colorTextLight,
     whiteSpace: 'nowrap',
     overflow: 'hidden',
     textOverflow: 'ellipsis'
   }),
   clearDescription: css({
     marginTop: tokens.spacingXs,
     color: tokens.colorTextLightest
   })
 };

function FeatureFlag(props) {

   const [createFlag, setCreateFlag] = useState(false);

   const createNewFeatureFlag = (featureFlag) => {
      console.log('here: ',featureFlag);
   }

  return (
    <React.Fragment>
      <div className={styles.flexContainer}>
         <Heading element='h2' className={styles.heading}>Feature Flag:</Heading>
         <Button variant='primary' size='small' startIcon={<PlusIcon />} onClick={() => setCreateFlag(true)}>Create Feature Flag</Button>
      </div>
      <CreateFeatureFlagModal setCreateFlag = {setCreateFlag} onModalClose={createNewFeatureFlag}/>
      <Modal onClose={() => props.onModalClose()}>
         {() => (
         <>
            <Modal.Header
               title="Create Feature Flag"
               subtitle="subtitle"
            />
            <Modal.Content>
               <Heading>
                  First entry published! It can now be fetched via the APIs
               </Heading>
               <Paragraph>
                  To discover more about how to consume content from the APIs, go
                  to Space Home.
               </Paragraph>
            </Modal.Content>
         </>
         )}
      </Modal>
      <Select isRequired
         id='feature-flag-selection'
         name='feature-flag-selection'
         value={props.featureFlag? props.featureFlag.id: ''}
         isDisabled={!props.featureFlags.length}
         size='medium'
         className={styles.select}
         onChange={(e) => {
            const value = e.target.value;
            if(value === -1){
               props.onFeatureFlagChange({
                  name: '',
                  id: '',
                  status: '-'
               });
            } else{
               const selectedFeatureFlag = props.featureFlags.find(featureFlag => featureFlag.id === value);
               props.onFeatureFlagChange(selectedFeatureFlag);
            }
         }}>
         <Select.Option>Select VWO Feature Flag</Select.Option>
         {
            props.featureFlags.map((featureFlag) => {
               return <Select.Option key={featureFlag.id} value={featureFlag.id}>
                  {featureFlag.name} ({featureFlag.status})
               </Select.Option>
            })
         }
      </Select>
    </React.Fragment>
  )
}

export default FeatureFlag;
