import { Button, EntryCard, Menu, MenuItem, Stack, Subheading, TextLink } from '@contentful/f36-components';
import CreateContent from './CreateContent';

const styles = {
   container: css({
     marginTop: '-10px'
   }),
   item: css({
     marginBottom: '0px'
   }),
   subheading: css({
      marginBottom: '-15px'
   }),
   menuList: css({
      maxHeight: '200px'
   }),
   menuListHeader: css({
      marginLeft: tokens.spacingS,
