import { Flex, Card, Grid, Text, Heading, Paragraph, Stack } from '@contentful/f36-components';
import tokens from '@contentful/f36-tokens';
import { css } from 'emotion';
import React from 'react';
import VariationItem from './VariationItem';

const styles = {
   heading: css({
      marginBottom: '10px'
   }),
   container: css({
    width: '100%'
   })
}

function Variations(props) {
  console.log('variations: ',props.variations)
  return (
    <React.Fragment>
      <Heading element='h2' className={styles.heading}>Variations:</Heading>
      <Paragraph>Content created in this feature flag is available only for this feature flag</Paragraph>
      <Flex flexDirection='column' justifyContent='center' marginLeft='spacingM' marginTop='spacingL' gap='spacingL' className={styles.container}>
          <Stack>
            {props.variations.map(variation => {
                return <VariationItem variation={variation}/>
            })}
          </Stack>
      </Flex>
    </React.Fragment>
  )
}

export default Variations;
