import React, {useState,useEffect} from 'react';
import { Heading, Select } from '@contentful/f36-components';
import { /* useCMA, */ useSDK } from '@contentful/react-apps-toolkit';

const EntryEditor = () => {
  const sdk = useSDK();
  console.log('here1: ',sdk)
  const [id,setId] = useState(sdk.field?.getValue());
  const [ids,setIds] = useState([
    {name: 'id1',id: 'id1',selected: false},
    {name: 'id2',id: 'id2',selected: false},
    {name: 'id3',id: 'id3',selected: false},
    {name: 'id4',id: 'id4',selected: false},
    {name: 'id5',id: 'id5',selected: false}
  ]);

  async function getAllEntries() {
    sdk.space.getEntries({}).then((resp) => {
      console.log('resp: ',resp);
      if(resp.items.length){
        let newIds = ids.map(id => {
          let isSelected = id.selected;
          for(let i = 0; i < resp.items.length; i++){
            let entry = resp.items[i];
            if(entry.fields[sdk.field?.id] && id.id === entry.fields[sdk.field?.id]['en-US']){
              isSelected = true;
              break;
            }
          }
          return {...id,selected: isSelected && id.id !== sdk.field?.getValue()}
        })
        setIds(newIds);
        if(!id){
          setDefaultId(newIds);
        }
      }
    })
    .catch((error) => {
      console.log("error: ",error)
    })
  }

  const setUserId = (value) => {
    sdk.field?.setValue(value);
    setId(value);
  }

  const setDefaultId = (newIds) => {
    for(let i = 0; i < newIds.length; i++){
      let userId = newIds[i];
      if(!userId.selected){
        setUserId(userId.id);
        break;
      }
    }
  }

  useEffect(() => {
    sdk.window?.startAutoResizer();
    getAllEntries();
  },[]);

  return (
    <React.Fragment>
      <Heading>Select User Id</Heading>
      <Select
        value={id}
        onChange={e => setUserId(e.target.value)}
        id='userId'
        name='userId'>
          {ids.map((uniqueId) => {
              return <Select.Option isDisabled={uniqueId.selected} value={uniqueId.id} key={uniqueId.id}>{uniqueId.name}</Select.Option>
          })}
      </Select>
    </React.Fragment>
  )
};

export default EntryEditor;
