import React, { useEffect, useState } from 'react';
import { css } from 'emotion';
import { CheckCircleIcon, InfoCircleIcon, ChevronRightIcon } from '@contentful/f36-icons';
import tokens from '@contentful/f36-tokens';
import { Status } from './constants';

const styles = {
  note: css({
    marginBottom: tokens.spacingL
  }),
  container: css({
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'space-between',
    marginTop: '30px',
    maxWidth: 1000
  }),
  item: css({
    display: 'flex',
    alignItems: 'center',
    fontSize: tokens.fontSizeM,
    color: tokens.colorTextMid
  }),
  itemSeparator: css({
    marginLeft: tokens.spacingM,
    marginRight: tokens.spacingM
  }),
  itemIcon: css({
    marginRight: tokens.spacingS
  })
};

function StatusItem(props) {
   if(props.active){
      return (
         <div className={styles.item}>
           <CheckCircleIcon
             className={styles.itemIcon}
             color={'positive'}
             size="small"
           />
           <span>{props.children}</span>
         </div>
       );
   }
   else{
      return (
        <div className={styles.item}>
          <InfoCircleIcon
            className={styles.itemIcon}
            color={'muted'}
            size="small"
          />
          <span>{props.children}</span>
        </div>
      );
   }
 }

function StatusSeparator() {
   return <ChevronRightIcon className={styles.itemSeparator} size="small" color="muted" />;
 }

function StatusBar(props) {
   let statuses = {
      [Status.SelectFeatureFlag]: false,
      [Status.StartFeatureFlag]: false,
      [Status.AddContent]: false,
      [Status.PublishVariations]: false
    };
    const [status, setStatus] = useState(statuses);

    const updateStatus = (currentStep) => {
      if (!currentStep) {
        return statuses;
      }
      let _status = status;
      if(currentStep > 1){
        _status[Status.SelectFeatureFlag] = true;
      }
      if(currentStep > 2){
        _status[Status.AddContent] = true;
       }
      if(currentStep > 3){
        _status[Status.PublishVariations] = true;
      }
      setStatus(_status);
    };

    useEffect(() => {
      console.log('step: ',props.currentStep)
      updateStatus(props.currentStep);
    },[props.currentStep]);

  return (
    <div className={styles.container}>
      <StatusItem active={status[Status.SelectFeatureFlag]}>Create feature flag</StatusItem>
      <StatusSeparator />
      <StatusItem active={status[Status.AddContent]}>Add variations and content</StatusItem>
      <StatusSeparator />
      <StatusItem active={status[Status.PublishVariations]}>Publish variations</StatusItem>
    </div>
  )
}

export default StatusBar;
