import React, {useState} from 'react';
import { css } from 'emotion';
import tokens from '@contentful/f36-tokens';
import { PlusIcon } from '@contentful/f36-icons';
import { Button, EntryCard, Menu, MenuItem, Stack, Subheading, TextLink } from '@contentful/f36-components';

const styles = {
   container: css({
     marginTop: '-10px'
   }),
   item: css({
     marginBottom: '0px'
   }),
   subheading: css({
      marginBottom: '0px'
   }),
   menuList: css({
      maxHeight: '200px'
   }),
   menuListHeader: css({
      marginLeft: tokens.spacingS,
      fontWeight: 'bold'
   })
 };


function VariationItem(props) {
   const [isDropdownShown, setShowDropdown] = useState(false);

   const createEntry = (entry) => {
      props.onCreateVariation(props.variation, entry);
   }

   const isContentAdded = props.variation.jsonContent.value != 'notSet';

  return (
    <React.Fragment>
      <Subheading className={styles.subheading}>{props.variation.name}</Subheading>
      {!isContentAdded && <Stack alignItems='start' flexDirection='column' spacing='spacingXs' className={styles.container}>
         <Menu>
            <Menu.Trigger>
               <TextLink
                  as="button"
                  variant="primary"
                  icon={<PlusIcon />}
                  alignIcon="start"
                  className={styles.item}
                  >Create entry and link</TextLink>
            </Menu.Trigger>
            <Menu.List className={styles.menuList}>
               <Menu.ListHeader className={styles.menuListHeader}>Select content type</Menu.ListHeader>
               {props.contentTypes.map(contentType => {
                  return <Menu.Item key={contentType.sys.id} onClick={() => createEntry(contentType)}>{contentType.name}</Menu.Item>
               })}
            </Menu.List>
         </Menu>
         <TextLink
            as="button"
            variant="primary"
            icon={<PlusIcon />}
            alignIcon="start"
            onClick={() => props.linkExistingEntry(props.variation)}
            className={styles.item}
            >Link an existing entry</TextLink>
      </Stack>}
      {isContentAdded && <EntryCard 
         status='draft'
         contentType='Author'
         title='title'
         description='some random description'
         actions={[
            <MenuItem key='edit' onClick={props.editContent(props.variation)}>Edit</MenuItem>,
            <MenuItem key='edit' onClick={props.removeContent(props.variation)}>Remove</MenuItem>,
         ]}/>}
    </React.Fragment>
  )
}

export default VariationItem;
