import { Flex, Button, Grid, Text, Heading, Paragraph, Stack } from '@contentful/f36-components';
import tokens from '@contentful/f36-tokens';
import { css } from 'emotion';
import { PlusIcon } from '@contentful/f36-icons';
import { mapVwoVariationsAndContent } from '../utils';
import React, { useCallback, useEffect } from 'react';
import VariationItem from './VariationItem';

const styles = {
   heading: css({
      marginBottom: '10px'
   }),
   container: css({
    width: '100%'
   })
}

function Variations(props) {
  const removeContent = (vwoVariation) => {
    const values = props.sdk.entry.fields.variations.getValue() || [];
    const meta = props.sdk.entry.fields.meta.getValue() || {};

    if(vwoVariation){
      delete meta[vwoVariation.id];
    }
    props.sdk.entry.fields.meta.setValue(meta);
    props.sdk.entry.fields.variations.setValue(values.filter(value => value.sys.id != vwoVariation.jsonContent.value));
    props.updateVwoVariationContent(vwoVariation.id, 'notSet');
  }

  const mappedVariations = mapVwoVariationsAndContent(props.vwoVariations, props.entries, props.contentTypes, props.sdk.locales.default);
  console.log('mappedVariations: ',mappedVariations,props.entries);

  return (
    <React.Fragment>
      <Flex alignItems='center' justifyContent='space-between'>
        <Heading element='h2' className={styles.heading}>Variations:</Heading>
        <Button variant='primary' size='small' startIcon={<PlusIcon />} onClick={props.addVwoVariation}>Add Variation</Button>
      </Flex>
      <Paragraph>Content created in this feature flag is available only for this feature flag</Paragraph>
      <Flex flexDirection='column' justifyContent='center' marginLeft='spacingM' marginTop='spacingL' gap='spacingXl' className={styles.container}>
        {mappedVariations.map(variation => {
            return <VariationItem
              variation={variation}
              linkExistingEntry={props.linkExistingEntry}
              contentTypes={props.contentTypes}
              onCreateVariation={props.createVariation}
              editContent = {editContent}
              removeContent = {removeContent}
              key={variation.vwoVariation.id}/>
        })}
      </Flex>
    </React.Fragment>
  )
}

export default Variations;
