import { Card, Button,Heading, Box, Text, Paragraph, Flex, Grid, Subheading } from '@contentful/f36-components';
import React, {useState} from 'react';
import { css } from 'emotion';
import tokens from '@contentful/f36-tokens';
import { PlusIcon } from '@contentful/f36-icons';
import CreateFeatureFlagModal from './CreateFeatureFlagModal';

const styles = {
   flexContainer: css({
      display: 'flex',
      alignItems: 'center',
      width: '100%',
      justifyContent: 'space-between',
      marginBottom: tokens.spacingL
   }),
   tile: css({
      width: '100%',
      height: '180px'
   })
 };

function FeatureFlag(props) {

   const [createFlag, setCreateFlag] = useState(false);
   const [featureFlag, setFeatureFlag] = useState({});

   const createNewFeatureFlag = (featureFlag) => {
      console.log('here: ',featureFlag);
      setFeatureFlag(featureFlag);
      if(featureFlag.featureKey){
         setCreateFlag(false);
      }
      props.onFeatureFlagCreation(featureFlag);
   }
   console.log('sdk: ',props.sdk)

   const isFeatureFlagCreated = featureFlag.featureKey;

  return (
    <React.Fragment>
      <Heading element='h2'>Feature Flag:</Heading>
      {!isFeatureFlagCreated && <Flex alignItems='center' flexDirection='column' justifyContent='center' className={styles.tile}>
         <Paragraph element='h6'>Create a Feature flag in order to create Variations</Paragraph>
         <Button variant='primary' size='small' startIcon={<PlusIcon />} onClick={() => setCreateFlag(true)}>Create Feature Flag</Button>
      </Flex>}
      {isFeatureFlagCreated && <Box padding="spacingL" style={{width: '500px'}}>
         <Card padding="spacingL" radius="medium">
            <Grid rowGap="spacingM" columns='1fr 2fr' columnGap='spacingM' style={{backgroundColor: 'yellow'}}>
               <Grid.Item>
                  <Text as="h4" fontWeight="fontWeightBold">Feature Name:</Text>
                  <Paragraph>{featureFlag.name}</Paragraph>
               </Grid.Item>

               <Grid.Item>
                  <Text as="h4" fontWeight="fontWeightBold">Feature Key:</Text>
                  <Paragraph>{featureFlag.featureKey}</Paragraph>
               </Grid.Item>

               <Grid.Item>
                  <Text as="h4" fontWeight="fontWeightBold">Feature Type:</Text>
                  <Paragraph>{featureFlag.featureType}</Paragraph>
               </Grid.Item>

               <Grid.Item>
                  <Text as="h4" fontWeight="fontWeightBold">Contentful ID:</Text>
                  <Paragraph>{featureFlag.id}</Paragraph>
               </Grid.Item>

               <Grid.Item>
                  <Text as="h4" fontWeight="fontWeightBold">Description:</Text>
                  <Paragraph>{featureFlag.description}</Paragraph>
               </Grid.Item>
            </Grid>
         </Card>
      </Box>}
      <CreateFeatureFlagModal setCreateFlag={setCreateFlag} onModalClose={createNewFeatureFlag} isShown={createFlag}/>
    </React.Fragment>
  )
}

export default FeatureFlag;
