import React, {useState} from 'react';
import { css } from 'emotion';
import tokens from '@contentful/f36-tokens';
import { PlusIcon } from '@contentful/f36-icons';
import { Button, EntryCard, Menu, MenuItem, Stack, Subheading, TextLink } from '@contentful/f36-components';
import CreateContent from './CreateContent';

const styles = {
   subheading: css({
      marginBottom: '-15px'
   })
 };


function VariationItem(props) {

   const isContentAdded = props.variation.entry && props.variation.vwoVariation.jsonContent.value != 'notSet';
  return (
    <React.Fragment>
      <Subheading className={styles.subheading}>{props.variation.vwoVariation.name}</Subheading>
      {!isContentAdded && <CreateContent 
         variation={props.variation}
         contentTypes={props.contentTypes}
         linkExistingEntry={props.linkExistingEntry}
         onCreateEntry={props.onCreateVariation}/>}
      {isContentAdded && <EntryCard 
         status={props.variation.entry.status}
         contentType={props.variation.entry.contentType}
         title={props.variation.entry.title}
         description={props.variation.entry.description}
         onClick={() => props.editContent(props.variation.vwoVariation)}
         actions={[
            <MenuItem key='edit' onClick={() => props.editContent(props.variation.vwoVariation)}>Edit</MenuItem>,
            <MenuItem key='remove' onClick={() => props.removeContent(props.variation.vwoVariation)}>Remove</MenuItem>,
         ]}/>}
    </React.Fragment>
  )
}

export default VariationItem;
