import React, {useState,useEffect} from 'react';
import { Button, Heading, Paragraph, Select, Modal } from '@contentful/f36-components';
import useMethods from 'use-methods';
import { /* useCMA, */ useSDK } from '@contentful/react-apps-toolkit';
import { css } from 'emotion';
import VWOLogo from '../components/VWOLogo';
import StatusBar from '../components/StatusBar';
import SectionSplitter from '../components/SectionSplitter';

const GlobalStateContext = React.createContext(null);

const styles = {
  connect: css({
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    '&:hover': {
      backgroundColor: 'transparent!important' // necessary to eliminate the forma styling in favor of the custom optimizely styling
    }
  })
};

const methods = state => {
  return {
    setInitialData({featureFlags, contentTypes, referenceInfo }){
      state.featureFlags = featureFlags;
      state.contentTypes = contentTypes;
      state.referenceInfo = referenceInfo;
      state.loading = false;
    },
    setError(message){
      state.error = message;
    },
    setFeatureFlagId(id){
      state.featureFlagId = id;
      state.meta = {};
    },
    setVariations(variations){
      state.variations = variations;
    },
    setMeta(meta) {
      state.meta = meta;
    },
    updateFeatureFlag(id, _featureFlag){
      const index = state.featureFlags.findIndex(
        featureFlag => featureFlag.id.toString() === id.toString()
      );
      if (index !== -1) {
        state.featureFlags[index] = _featureFlag;
      }
    }
  }
}

const getInitialValues = sdk => ({
  loading: true,
  error: false,
  featureFlags: [],
  contentTypes: [],
  meta: sdk.entry.fields.meta?.getValue() || {},
  variations: sdk.entry.fields.variations?.getValue() || [],
  featureFlagId: sdk.entry.fields.featureFlagId?.getValue(),
  entries: {}
})

const fetchInitialData = async (sdk, client) => {
  const { space, ids, locales } = sdk;
  const [contentTypes, entries, featureFlags] = await Promise.all([
    space.getContentTypes({ order: 'name', limit: 1000}),
    space.getEntries({ links_to_entry: ids.entry, skip: 0, limit: 1000}),
    client.getFeatureFlags()
  ]);

  return {
    featureFlags,
    contentTypes: contentTypes.items,
    referenceInfo: {
      entries: entries
    }
  }
}



const EntryEditor = (props) => {
  const globalState = useMethods(methods, getInitialValues(props.sdk));
  const [state, actions] = globalState;

  useEffect(() => {
    fetchInitialData(props.sdk, props.client)
      .then(data => {
        actions.setInitialData(data);
        return data;
      })
      .catch(() => {
        actions.setError('Unable to load initial data');
      });
  }, [actions, props.client, props.sdk]);

  return (
    <React.Fragment>
      <GlobalStateContext.Provider value={globalState}>
        <Modal title='Connect with VWO' isShown={props.client}>
          <Paragraph testId='reconnect-vwo'>
            Your VWO session has expired. Reconnect to continue editing.
          </Paragraph>
          <Button
            className={styles.connect}
            onClick={props.openAuth}
            testId='connect-button'
            isFullWidth
            buttonType='naked'>
          </Button>
          <VWOLogo />
        </Modal>

        <div testId='editor-page'>
          <StatusBar />
          <SectionSplitter />
        </div>
      </GlobalStateContext.Provider>
    </React.Fragment>
  )
};

export default EntryEditor;
