import React from 'react';
import { css } from 'emotion';
import PropTypes from 'prop-types';
import { CheckCircleIcon, InfoCircleIcon, ChevronRightIcon } from '@contentful/f36-icons';
import tokens from '@contentful/f36-tokens';
import { Status } from './constants';

const styles = {
  note: css({
    marginBottom: tokens.spacingL
  }),
  container: css({
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'space-between',
    maxWidth: 1000
  }),
  item: css({
    display: 'flex',
    alignItems: 'center',
    fontSize: tokens.fontSizeM,
    color: tokens.colorTextMid
  }),
  itemSeparator: css({
    marginLeft: tokens.spacingM,
    marginRight: tokens.spacingM
  }),
  itemIcon: css({
    marginRight: tokens.spacingS
  })
};

function StatusItem(props) {
   if(props.active){
      return (
         <div className={styles.item}>
           <CheckCircleIcon
             className={styles.itemIcon}
             color={'positive'}
             size="small"
           />
           <span>{props.children}</span>
         </div>
       );
   }
   else{
      return (
        <div className={styles.item}>
          <InfoCircleIcon
            className={styles.itemIcon}
            color={'muted'}
            size="small"
          />
          <span>{props.children}</span>
        </div>
      );
   }
 }

function StatusSeparator() {
   return <ChevronRightIcon className={styles.itemSeparator} size="small" color="muted" />;
 }

 const checkStatuses = (statuses, featureFlag, variations, entries) => {
   if (!featureFlag) {
     return statuses;
   }

   statuses[Status.SelectFeatureFlag] = true;
   if (featureFlag.status === 'running') {
     statuses[Status.StartFeatureFlag] = true;
   }
   return statuses;
 };

function StatusBar(props) {
   let statuses = {
      [Status.SelectFeatureFlag]: false,
      [Status.StartFeatureFlag]: false,
      [Status.AddContent]: false,
      [Status.PublishVariations]: false
    };

    if (props.loaded) {
      statuses = checkStatuses(statuses, props.experiment, props.variations, props.entries);
    }

  return (
    <div className={styles.container}>
      <StatusItem active={statuses[Status.SelectFeatureFlag]}>Select feature flag</StatusItem>
      <StatusSeparator />
      <StatusItem active={statuses[Status.AddContent]}>Add content</StatusItem>
      <StatusSeparator />
      <StatusItem active={statuses[Status.PublishVariations]}>Publish variations</StatusItem>
      <StatusSeparator />
      <StatusItem active={statuses[Status.StartFeatureFlag]}>Start feature flag</StatusItem>
    </div>
  )
}

export default StatusBar;
