import { Heading, Select } from '@contentful/f36-components';
import React, {useState} from 'react';
import { css } from 'emotion';
import tokens from '@contentful/f36-tokens';

const styles = {
   select: css({
      width: '400px'
   }),
   heading: css({
     marginBottom: tokens.spacingL
   }),
   description: css({
     marginTop: tokens.spacingS,
     color: tokens.colorTextLight,
     whiteSpace: 'nowrap',
     overflow: 'hidden',
     textOverflow: 'ellipsis'
   }),
   clearDescription: css({
     marginTop: tokens.spacingXs,
     color: tokens.colorTextLightest
   })
 };

function FeatureFlag(props) {

   const [featureFlag, selectFeatureFlag] = useState();

  return (
    <React.Fragment>
      <Heading element='h2' className={styles.heading}>Feature Flag:</Heading>
      <Select isRequired
         id='feature-flag-selection'
         name='feature-flag-selection'
         value={featureFlag}
         size='medium'
         className={styles.select}
         onChange={(e) => {
            const value = e.target.value;
            if(value === -1){
               props.onFeatureFlagChange({
                  name: '',
                  id: '',
                  status: '-'
               });
            } else{
               const selectedFeatureFlag = props.featureFlags.find(featureFlag => featureFlag.id === value);
               props.onFeatureFlagChange(selectedFeatureFlag);
            }
         }}>
         <Select.Option>Select VWO Feature Flag</Select.Option>
         {
            props.featureFlags.map((featureFlag) => {
               return <Select.Option key={featureFlag.id} value={featureFlag.id}>
                  {featureFlag.name} ({featureFlag.status})
               </Select.Option>
            })
         }
      </Select>
    </React.Fragment>
  )
}

export default FeatureFlag;
