export class VariableModel {
  private val: dynamic;
  private value: dynamic;

  private type: string;

  private k: string;
  private key: string;

  private i: number;
  private id: number;

  modelFromDictionary(variable: VariableModel): this {
    this.value = variable.val || variable.value;
    this.type = variable.type;
