import { Layout, Button,Heading, Header, Box, Form, FormControl, TextInput, Textarea, Stack, Radio, ModalControls } from '@contentful/f36-components';
import React, {useState} from 'react';
import { css } from 'emotion';
import tokens from '@contentful/f36-tokens';
import useMethods from 'use-methods';
import { PlusIcon } from '@contentful/f36-icons';
import CreateFeatureFlagModal from './CreateFeatureFlagModal';

const styles = {
   flexContainer: css({
      display: 'flex',
      alignItems: 'center',
      width: '100%',
      justifyContent: 'space-between',
      marginBottom: tokens.spacingL
   }),
   heading: css({
      height: '20px',
      marginRight: '100px'
   })
 };

 const FlagTypes = {
   TEMPORARY: 'temporary',
   PERMANENT: 'permanent'
};

 const initialData = ({
   flagName: '',
   featureKey: '',
   description: '',
   type: FlagTypes.TEMPORARY
 });

 const methods = state => {
   return {
      setFlagName(flagName){
         state.flagName = flagName;
      },
      setFeatureKey(featureKey){
         state.featureKey = featureKey;
      },
      setDescription(description){
         state.description = description;
      },
      setType(type){
         state.type = type;
      }
   }
 }

function FeatureFlag(props) {

   const [createFlag, setCreateFlag] = useState(false);
   const globalState = useMethods(methods,initialData);
   const [state, actions] = globalState;

   const createFeatureFlag = () => {
      const featureFlagCreated = {
         name: state.flagName,
         featureKey: state.featureKey,
         description: state.description,
         featureType: state.type
      };
      props.onModalClose(featureFlagCreated);
   }

   const createNewFeatureFlag = (featureFlag) => {
      console.log('here: ',featureFlag);
      setCreateFlag(false);
   }
   console.log('sdk: ',props.sdk)

  return (
    <React.Fragment>
      <Heading element='h2' className={styles.heading}>Feature Flag:</Heading>
      {/* <Button variant='primary' size='small' startIcon={<PlusIcon />} onClick={() => setCreateFlag(true)}>Create Feature Flag</Button> */}
      <Box padding='spacingS'>
         <Form onSubmit={createFeatureFlag}>
            <FormControl>
               <FormControl.Label isRequired>Flag name</FormControl.Label>
               <TextInput
                  maxLength={30}
                  value={state.flagName}
                  onChange={(e) => actions.setFlagName(e.target.value)}/>
            </FormControl>
            <FormControl>
               <FormControl.Label isRequired>Feature key</FormControl.Label>
               <TextInput
                  maxLength={30}
                  value={state.featureKey}
                  onChange={(e) => actions.setFeatureKey(e.target.value)}/>
            </FormControl>
            <FormControl>
               <FormControl.Label>Description</FormControl.Label>
               <Textarea
                  value={state.description}
                  onChange={(e) => actions.setDescription(e.target.value)}/>
            </FormControl>
            <FormControl>
               <FormControl.Label>Feature type</FormControl.Label>
               <Stack flexDirection='row'>
                  <Radio
                     id='flag-type1'
                     name='flag-type'
                     value={FlagTypes.TEMPORARY}
                     isChecked={state.type === FlagTypes.TEMPORARY}
                     onChange={() => actions.setType(FlagTypes.TEMPORARY)}>Temporary</Radio>
                  <Radio
                     id='flag-type2'
                     name='flag-type'
                     value={FlagTypes.PERMANENT}
                     isChecked={state.type === FlagTypes.PERMANENT}
                     onChange={() => actions.setType(FlagTypes.PERMANENT)}>Permanent</Radio>
               </Stack>
            </FormControl>
         </Form>
      </Box>
      {/* <CreateFeatureFlagModal setCreateFlag={setCreateFlag} onModalClose={createNewFeatureFlag} isShown={createFlag}/> */}
    </React.Fragment>
  )
}

export default FeatureFlag;
